var newsModel=new Vue({el:"#model-news",data:{Ad:"",CategoryMap:["0","53","54","55","56","57"],pageSize:6,currentpage:1,pagination:"",Pro:"",TopNews:""},updated:function(){},computed:{},methods:{getNewsList:function(e){var t=void 0!==e?e:"0";$("#loader").fadeIn(300),this.$http.get(headerModel.api+"/News/GetList?token="+headerModel.token+"&category="+t+"&newkey=0&pagesize="+this.pageSize+"&currentpage="+this.currentpage).then(function(e){this.Ad=e.data,this.pagination=e.data.pagecount,this.$nextTick(function(){this.bannerSlider()}),$("#loader").fadeOut(300)})},getIntPro:function(){this.$http.get(headerModel.api+"/ZhongChou/GetList?pagesize=2&state=0&currentpage=1&token="+headerModel.token).then(function(e){this.Pro=e.data})},bannerSlider:function(){var e=$("#news-banner");e.owlCarousel({items:1,loop:!0,autoplay:!0,autoplayTimeout:5e3,autoplayHoverPause:!0,nav:!1})},getIntNew:function(){this.$http.get(headerModel.api+"/News/GetTopRecommend?count=10&categoryid=0").then(function(e){this.TopNews=e.data})},timeFormart:function(e){var t=((new Date).getTime(),new Date(e));return DGDTOOLS.fT._convertTime(t)}},created:function(){this.getIntPro(),this.getNewsList(),this.getIntNew()}});